import React from 'react'
import './App.css'
import {useSelector,useDispatch } from "react-redux";
import {incNumber,decNumber} from './actions/index';
const App=()=>{
  const myState=useSelector((state=>state.changeTheNumber))
  const dispatch=useDispatch();
  return (
    <>
    <h1 className="ram">Add subtract operater</h1>
    <div className="quantity">
    <a className="quantity_plus" title="increment" onClick={()=>dispatch(incNumber())}><button>ADD</button></a>
      <a className="quantity_minus" title="decrement"onClick={()=>dispatch(decNumber())}><button>SUB</button></a>
      <input name="quantity" className="quantity1"type="text" value={myState}/>
    </div>
    </>
  )
}

export default App;
